package com.example.a5month_hw2

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.fragment.app.Fragment
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.GridLayoutManager
import com.example.a5month_hw2.databinding.FragmentSheepBinding

class SheepFragment : Fragment() {

    private lateinit var binding: FragmentSheepBinding
    private lateinit var adapter: SheepAdapter
    private val list = arrayListOf<Sheep>()
    private val selectedPhotos = mutableListOf<Sheep>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        binding = FragmentSheepBinding.inflate(layoutInflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        loadData()
        adapter = SheepAdapter(list, this::addImage, this::removeImage, this::btnVisible)
        binding.apply {
            recyclerView.layoutManager = GridLayoutManager(context, 3)
            recyclerView.adapter = adapter
        }

        initListener()
    }

    private fun addImage(item: Sheep) {
        selectedPhotos.add(item)
    }

    private fun removeImage(item: Sheep) {
        selectedPhotos.remove(item)
    }

    private fun btnVisible(sheep: Sheep) {
        if (!sheep.isSelected) {
            binding.btnSend.visibility = View.GONE
        } else {
            binding.btnSend.visibility = View.VISIBLE
        }
    }

    private fun initListener() {
        binding.btnSend.setOnClickListener {
            val bundle = Bundle().apply {
                putSerializable(fFragSFrag, ArrayList(selectedPhotos))
            }
            list.clear()
            selectedPhotos.clear()
            findNavController().navigate(R.id.selectedBarashekFragment, bundle)
        }
    }

